import Event from '../Event';
export default class Message {
    private static typeId;
    /** 消息发起对象 */
    private thisObj;
    /** 注册项列表 */
    private registerList;
    /** 创建消息类型 */
    static createType: (name: string) => string;
    constructor(thisObj?: any);
    /**
     * 发送消息
     * @param event 消息
     */
    send: (event: Event) => void;
    /**
     * 注册消息
     * @param type 消息类型
     * @param method 注册的代理函数
     */
    on: (type: string, method: (e: Event) => void) => void;
    /**
     * 注销消息
     * @param type 消息类型
     * @param method 注册的代理函数
     */
    off: (type: string, method: (e: Event) => void) => void;
    /**
     * 获取注册列表的索引
     * @param type 消息类型
     * @param method 注册的代理函数
     */
    private getIndex;
}
